import 'package:flutter/material.dart';
import 'package:igame/pages/dc/card/person.dart';
import 'package:igame/themes.dart';

class DCardFiveStarsWidget extends StatefulWidget {

  // TODO: add state variables, methods and constructor params
  DCardFiveStarsWidget({Key key}):super(key: key);

  @override
  _DCardFiveStarsWidgetState createState() => _DCardFiveStarsWidgetState();
}

class _DCardFiveStarsWidgetState extends State<DCardFiveStarsWidget> {
  @override
  Widget build(BuildContext context) {
    // TODO: implement build
    return Scaffold(
      appBar: AppBar(
        backgroundColor: Colors.amber,
        title: Text('五星图鉴'),
      ),
      body: Container(
        padding: EdgeInsets.symmetric(horizontal: space),
        decoration: BoxDecoration(
          color: Colors.black87,
        ),
        child: GridView.builder(
          gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(crossAxisCount: 6),
          itemCount: stars.length,
          itemBuilder: (context, index) {
            return GestureDetector(
              onTapUp: (TapUpDetails details) {
                Navigator.of(context).push(MaterialPageRoute(
                  builder: (context) => DCardPersonWidget(stars[index]),
                ));
              },
              child: Container(
                padding: EdgeInsets.all(3.0),
                child: Image.network(stars[index]),
              ),
            );
          },
        ),
      ),
    );
  }
}


